import React, { useRef } from "react";
import Input from "../../UI/Input";

import classes from "./MealItemForm.module.css";

const MealItemForm = (props) => {
  const enteredNum = useRef();

  const numberOfItemsHandler = (event) => {
    event.preventDefault();
    const num = enteredNum.current.val;
    console.log(num);
  };
  return (
    <form onSubmit={numberOfItemsHandler} className={classes.form}>
      <Input
        label="Amount"
        ref={enteredNum}
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: 1,
          max: 5,
          step: 1,
          defaultValue: 1,
        }}
      />
      <button type="submit"> +Add</button>
    </form>
  );
};

export default MealItemForm;
